public partial class Personal_Updatedesk : System.Web.UI.Page
{
    public JavaScriptSerializer jss = new JavaScriptSerializer();
    protected void Page_Load(object sender, EventArgs e)
    {
        if (!Page.IsPostBack)
        {
            if (LCX.Common.PublicMethod.GetSessionValue("UserName") == null)
            {
                Response.Write("");
                Response.End();
                return;
            }

            if (Request.QueryString["action"] != null)
            {
                String mstype = Request.QueryString["action"].ToString();
                switch (mstype)
                {
                    case "desktop": { updateDesk(Request.QueryString["icon_id"].ToString()); break; }  //更新桌面图标
                    case "getcount": { getCount(); break; }  //获取有多少人员
                    case "sms": { sms(); break; }  //获取短消息和微信消息是否有新的
                    case "theme": { updateTheme(Int32.Parse(Request.QueryString["themeid"].ToString())); break; } //更新主题
                    case "getmsg": { getMesg(); break; }   //获新取消息内容
                    case "redmsg": { redMesg(Int32.Parse(Request.QueryString["SMS_ID"].ToString())); break; }  //已经阅读过
                    case "updateMsg": { updateMsg(Request.QueryString["SMS_ID"].ToString()); break; }  //更新短消息阅讯状态
                    case "weather": { updateWeather(Request.QueryString["WEATHER_CITY"].ToString()); break; }  //更新天气状态
                    case "zaigang": { updateZaigang(Int32.Parse(Request.QueryString["ON_STATUS_SET"].ToString())); break; }
                    case "Online": { GetOnlineCount(); break; }
                        
                }
                return;
            }
        }
    }

    private void updateMsg(String msg)
    {
        if (msg != "")
        {
            string[] sArray = msg.Split(new Char[] { ',' });
            foreach ( string ID in sArray)
            {
                LCX.DBUtility.DbHelperSQL.ExecuteSQL("update LCXLanEmail set EmailState='已读' where EmailState='未读' and ID=" + Int32.Parse(ID));
            }
            Response.Write("+ok");  //更新所有的短消息
            Response.End();

        }
    }

    private void getCount()
    {
        Hashtable ht = new Hashtable();
        ht.Add("email", newmail());  
                //{"email":"115","notify":"0","workflow":"208","calendar":"0","attendance":"1","file_folder":"0"}
        Response.Write(jss.Serialize(ht));
        Response.End();
    }
    //获取是否有新的短消息 和 微信
    private void sms()
    {
        int NewMailCount = int.Parse(LCX.DBUtility.DbHelperSQL.GetSHSLInt("select count(*) from LCXLanEmail where ToUser='" + LCX.Common.PublicMethod.GetSessionValue("UserName") + "' and EmailState='未读'"));
        int NewMessage = 0; //微信
        if (NewMailCount > 0)
        {
            NewMailCount = 1;
        }
        Response.Write(NewMailCount+""+NewMessage);
        Response.End();
    } 
    //获取未读的新邮件数 提示信息显示
    private void getMesg()
    {
        LCX.BLL.LCXLanEmail MyLanEmail = new LCX.BLL.LCXLanEmail();
        DataSet ds = MyLanEmail.GetList("ToUser='" + LCX.Common.PublicMethod.GetSessionValue("UserName") + "' and EmailState='未读' order by ID desc");
        if (ds.Tables[0].Rows.Count > 0)
        {
            List<Hashtable> a = new List<Hashtable>();
            foreach (DataRow dr in ds.Tables[0].Rows)
            {
                Hashtable msg = new Hashtable();
                msg.Add("sms_id", dr["ID"]);
                msg.Add("to_id", 100);
                msg.Add("from_id", dr["FromUser"]);
                msg.Add("from_name", dr["FromUser"]);
                msg.Add("type_id", 1);
                msg.Add("type_name", "消息提醒");
                msg.Add("send_time", DateStringFromNow(Convert.ToDateTime(dr["TimeStr"])));
                msg.Add("unread", 1);
                msg.Add("content", dr["EmailTitle"]);
                msg.Add("url", "../LanEmail/EmailView.aspx?ID=" + dr["ID"]);
                msg.Add("receive", 1);
                a.Add(msg);
            }
            Response.Write(jss.Serialize(a).ToString());
        }
        else
        {
            Response.Write("null");
        }   
        Response.End();      
    }
    //设置为消息为已经读取
    private void redMesg(int ID)
    {
        LCX.DBUtility.DbHelperSQL.ExecuteSQL("update LCXLanEmail set EmailState='已读' where EmailState='未读' and ID=" + ID);
        Response.Write("11");
        Response.End();
    }
     //获取新邮件
    private int newmail()
    {
        int NewMailCount = int.Parse(LCX.DBUtility.DbHelperSQL.GetSHSLInt("select count(*) from LCXLanEmail where ToUser='" + LCX.Common.PublicMethod.GetSessionValue("UserName") + "' and EmailState='未读'"));
        return NewMailCount;
    }
     //更新用户的桌面
    private void updateDesk(string desktop)    {      
        LCX.Common.PublicMethod.CheckSession();
        LCX.BLL.LCXUser MyModel = new LCX.BLL.LCXUser();
        MyModel.ID = int.Parse(LCX.Common.PublicMethod.GetSessionValue("UserID"));
        MyModel.Desktop = desktop;
        MyModel.UpdateDesktop();
        Response.Write("+ok");
        Response.End();
    }
     //更新用户的主题
    private void updateTheme(int themeid)
    {  
        LCX.Common.PublicMethod.CheckSession();
        LCX.BLL.LCXUser MyModel = new LCX.BLL.LCXUser();
        MyModel.ID = int.Parse(LCX.Common.PublicMethod.GetSessionValue("UserID"));
        MyModel.Theme = themeid;
        MyModel.UpdateTheme();          
        Response.Write("+ok");
        Response.End();
    }
